storedProcedure.procedure:
CREATE OR REPLACE Function FPKG_DM_AC_CURR_FWD_ADJ(I_DATE   Date,
                                             I_ENTITY Varchar2) Return Number

Authid Current_User

 As
  Pragma Autonomous_Transaction;

  ERRNO     Number := 0;
  ERRMSG    Varchar(400);
  V_FNCNAME Varchar(400) := 'FPKG_DM_AC_CURR_FWD_ADJ';
  I_DT      Varchar(8) := TO_CHAR(I_DATE, 'YYYYMMDD');
Begin
  PKG_DM_AC_CURR_FWD_ADJ.P_BATCH(ERRNO, ERRMSG, I_DT, I_ENTITY);
  Return ERRNO;
Exception
  When Others Then
    ERRNO  := -1;
    ERRMSG := V_FNCNAME || ' had error!!' || Sqlerrm;
    DBMS_OUTPUT.PUT_LINE(ERRMSG);
    Return ERRNO;
End;
